public class Main {
    public static void main(String[] args) {
        AbstractCipher vigenereCipher = new VigenereCipher("key".toCharArray(), "Ichwillnichtmitihmtanzen".toCharArray());
        System.out.println(vigenereCipher.encode());

        char[][] key = {
                {6, 3},
                {5, 3}
        };
        char[] text = "".toCharArray();

        AbstractCipher hillCipher = new HillCipher(key, text);
        System.out.println(hillCipher.decode());
    }
}
